package sesi_02;

public class OperatorPembanding {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 88;
		int y = 90;

		System.out.println("x == y = " + (x == y));
		System.out.println("x != y = " + (x != y));
		System.out.println("x > y = " + (x > y));
		System.out.println("x < y = " + (x < y));
		System.out.println("x >= y = " + (x >= y));
		System.out.println("x <= y = " + (x <= y));
	}

}
